const shorten = (title) => {
  const splitTitle = title.split(" ");
  const newTitle = ` ${splitTitle[0]} ${splitTitle[1]} `;
  return newTitle;
};

const IsInCard = (state, id) => {
  const result = !!state.selectedItem.find((item) => item.id === id);
  return result;
};

export { shorten, IsInCard };
