import React from "react";

import styles from "../assets/css/branding.module.css";
const Search = () => {
  const { Searching, formSearch } = styles;
  //get input
  const input = document.getElementById("search");
  //get list of value
  const list = document.querySelectorAll(".list li");

  //function search on the list.
  function search() {
    for (const i = 0; i < list.length; i += 1) {
      //check if the element contains the value of the input
      if (list[i].innerText.toLowerCase().includes(input.value.toLowerCase())) {
        list[i].style.display = "block";
      } else {
        list[i].style.display = "none";
      }
    }
  }

  //to the change run search.
  input.addEventListener("input", search);
  return (
    <section className={Searching}>
      <h1>Search ...</h1>
      <div className={formSearch}>
        <input id="search" type="text" placeholder="Search..." />
      </div>
    </section>
  );
};

export default Search;
