import React, { useState, useRef } from "react";

import styles from "../assets/css/branding.module.css";
const Search = () => {
  const { Searching, formSearch } = styles;
  const [text, setText] = useState({ search: "" });

  const searchTimeoutRef = useRef();

  function handleOnChangeValue(event) {
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }
    setText(event.target.value);
    searchTimeoutRef.current = setTimeout(() => {
      // Call API : axios.post('url',{value:e.target.value})
    }, 1000);
  }
  return (
    <section className={Searching}>
      <h1>Search ...</h1>
      <div className={formSearch}>
        <input
          //   onChange={(event) => setText({ search: event.target.value })}
          onChange={handleOnChangeValue()}
          type="text"
          value={text.search}
          placeholder="Search"
        />
        <span>{text.search}</span>
      </div>
    </section>
  );
};

export default Search;
